SELECT l.*, description.*, excerpt.*, approver.*, rejecter.*, creator.*, updater.*, op_time.*, tag.*, img.* FROM location l INNER JOIN translation description ON l.description_id = description.id INNER JOIN translation excerpt ON l.excerpt_id = excerpt.id LEFT OUTER JOIN simple_profile approver ON true = true AND l.approved_by = approver.id LEFT OUTER JOIN simple_profile rejecter ON true = true AND l.rejected_by = rejecter.id LEFT OUTER JOIN simple_profile creator ON true = true AND l.created_by = creator.id LEFT OUTER JOIN simple_profile updater ON true = true AND l.updated_by = updater.id LEFT OUTER JOIN opening_time op_time ON op_time.location_id = l.id LEFT OUTER JOIN location_tag l_tag ON true AND l_tag.location_id = l.id LEFT OUTER JOIN tag ON true AND l_tag.tag_id = tag.id LEFT OUTER JOIN location_image l_img ON true AND l_img.location_id = l.id LEFT OUTER JOIN image img ON true AND l_img.image_id = img.id;
